import {useState, useEffect} from 'react';
import {Navbar, Container, Nav} from 'react-bootstrap';
import {BrowserRouter as Router} from "react-router-dom";
import instalogo from "/Users/unnathibaskar/portfolio/src/assets /insta.png";
import linkedinlogo from "/Users/unnathibaskar/portfolio/src/assets /linkedin.png";
import logo from "/Users/unnathibaskar/portfolio/src/assets /logo.png";
export const NavBar = () => {
    const[scrolled, setScrolled]=useState(false);
    const[activeLink, setActiveLink] = useState('homepage');
    useEffect(()=>{
        const onScroll=()=>{
            if(window.scrollY>50)
            {
                setScrolled(true);
            } 
            else
            {
                setScrolled(false);
            }
        }
        window.addEventListener("scroll",onScroll);
        return () =>window.removeEventListener("scroll",onScroll);
    },[])
    const onUpdateActiveLink = (value) =>{
        setActiveLink(value);
    }

    return(
        <Router>
            <NavBar>
                <Container>
                    <NavBar.Brand>
                        <a href="/"><img src={logo} alt=""/></a>
                    </NavBar.Brand>
                    <NavBar.Toggle>
                        <span className="navbar-toggler-icon"></span>
                    </NavBar.Toggle>
                    <NavBar.Collapse>
                        <Nav>
                            <Nav.Link href="#home" className={activeLink === 'homepage' ? 'active navbar-link' : 'navbar-link'} onClick={() => onUpdateActiveLink('homepage')}>Home</Nav.Link>
                            <Nav.Link href="#skills" className={activeLink === 'skills' ? 'active navbar-link' : 'navbar-link'} onClick={() => onUpdateActiveLink('skills')}>Skills</Nav.Link>
                            <Nav.Link href="#projects" className={activeLink === 'projects' ? 'active navbar-link' : 'navbar-link'} onClick={() => onUpdateActiveLink('projects')}>Projects</Nav.Link>
                        </Nav>
                        <span>
                            <div>
                                <a href="#"><img src={instalogo} alt=""/></a>
                                <a href="#"><img src={linkedinlogo} alt=""/></a>
                            </div>
                        </span>
                    </NavBar.Collapse>
                </Container>
            </NavBar>
        </Router>
    )
}
